mapper.select:
select 

from hero_games
where id = #{id,jdbcType=INTEGER}
  and game_name = #{gameName,jdbcType=VARCHAR}

mapper.delete:
delete from hero_games
where id = #{id,jdbcType=INTEGER}
  and game_name = #{gameName,jdbcType=VARCHAR}

mapper.update:
update hero_games


    winner_id = #{winnerId,jdbcType=INTEGER},


    loser_id = #{loserId,jdbcType=INTEGER},


    desc = #{desc,jdbcType=VARCHAR},


where id = #{id,jdbcType=INTEGER}
  and game_name = #{gameName,jdbcType=VARCHAR}

update hero_games
set winner_id = #{winnerId,jdbcType=INTEGER},
  loser_id = #{loserId,jdbcType=INTEGER},
  desc = #{desc,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
  and game_name = #{gameName,jdbcType=VARCHAR}
